import org.algorithm_visualizer.*;

import java.util.Arrays;

public class Main {

    private static ChartTracer chartTracer = new ChartTracer();

    private static LogTracer logTracer = new LogTracer("Console");

    private static Integer [] array = (Integer[]) new Randomize.Array1D(15, new Randomize.Integer(1, 20)).create();

    public static void main(String[] args) {

        int length = array.length;

        logTracer.printf("original array = %s\n",Arrays.toString(array));
        chartTracer.set(array);
        Layout.setRoot(new VerticalLayout(new Commander[]{chartTracer, logTracer}));
        Tracer.delay();

        boolean flag;

        for (int i = length - 1; i > 0; i--) {
            flag = true;
            for (int j = 0; j < i; j++) {
                chartTracer.select(j);
                chartTracer.select(j + 1);
                Tracer.delay();
                if (array[j] > array[j + 1]) {
                    logTracer.printf("swap %s and %s\n",array[j],array[j + 1]);
                    swap(j, j + 1, array);
                    flag = false;
                }
                chartTracer.deselect(j);
                chartTracer.deselect(j + 1);
            }
            if (flag) {
                break;
            }
        }


        logTracer.printf("sorted array = %s\n",Arrays.toString(array));

    }

    private static void swap(int x, int y, Integer [] array) {
        int temp = array[x];
        array[x] = array[y];
        array[y] = temp;
        chartTracer.patch(x, array[x]);
        chartTracer.patch(y, array[y]);
        Tracer.delay();
        chartTracer.depatch(x);
        chartTracer.depatch(y);
    }

}

// import visualization libraries {
const { Tracer, Array1DTracer, ChartTracer, LogTracer, Randomize, Layout, VerticalLayout } = require('algorithm-visualizer');
// }

// define tracer variables {
const chart = new ChartTracer();
const tracer = new Array1DTracer();
const logger = new LogTracer();
Layout.setRoot(new VerticalLayout([chart, tracer, logger]));
const D = Randomize.Array1D({ N: 15 });
tracer.set(D);
tracer.chart(chart);
Tracer.delay();
// }

// logger {
logger.println(`original array = [${D.join(', ')}]`);
// }
let N = D.length;
let swapped;
do {
  swapped = false;
  // visualize {
  tracer.select(N - 1);
  Tracer.delay();
  // }
  for (let i = 1; i < N; i++) {
    // visualize {
    tracer.select(i);
    Tracer.delay();
    // }
    if (D[i - 1] > D[i]) {
      // logger {
      logger.println(`swap ${D[i - 1]} and ${D[i]}`);
      // }
      const temp = D[i - 1];
      D[i - 1] = D[i];
      D[i] = temp;
      swapped = true;
      // visualize {
      tracer.patch(i - 1, D[i - 1]);
      tracer.patch(i, D[i]);
      Tracer.delay();
      tracer.depatch(i - 1);
      tracer.depatch(i);
      // }
    }
    // visualize {
    tracer.deselect(i);
    // }
  }
  // visualize {
  tracer.deselect(N - 1);
  // }
  N--;
} while (swapped);
// logger {
logger.println(`sorted array = [${D.join(', ')}]`);
// }

#include "algorithm-visualizer.h"

#define N   15
#define MIN 1
#define MAX 20

void BubbleSort(int start, int end, int array[]);

ChartTracer chartTracer("Chart");

int main() {
    int array[N];
    Randomize::Array1D<int>(N, *(new Randomize::Integer(MIN, MAX))).fill(&array[0]);
    chartTracer.set(array);
    Layout::setRoot(VerticalLayout({ chartTracer }));

    BubbleSort(0, N - 1, array);

    return 0;
}

void BubbleSort(int start, int end, int array[])
{
    chartTracer.select(end);

    int newEnd = start;
    for(int i = start; i < end; ++i)
    {
        chartTracer.select(i);
        chartTracer.select(i + 1);
        Tracer::delay();
        if(array[i] > array[i + 1])
        {
            std::swap(array[i], array[i + 1]);
            chartTracer.patch(i, array[i]);
            chartTracer.patch(i + 1, array[i + 1]);
            Tracer::delay();
            chartTracer.depatch(i);
            chartTracer.depatch(i + 1);
            newEnd = i;
        }

        chartTracer.deselect(i);
        chartTracer.deselect(i + 1);
    }

    if(newEnd == start)
    {
        return;
    }
    else
    {
        BubbleSort(start, newEnd, array);
    }
}
